mapper.select:
select case when sum(extract2) is null then 0 else sum(extract2) end total2, case when sum(extract1) is null then 0 else sum(extract1) end total1 from record


		and name = #{name,jdbcType=VARCHAR} 


		and name like concat('%', #{qname,jdbcType=VARCHAR}, '%') 


    	and expire_date >= #{startExpireDate, jdbcType=DATE}


    	and expire_date <= #{endExpireDate, jdbcType=DATE}


    	and create_time >= #{startCreateTime, jdbcType=TIMESTAMP}


    	and create_time <= #{endCreateTime, jdbcType=TIMESTAMP}


    	and update_time >= #{startUpdateTime, jdbcType=TIMESTAMP}


    	and update_time <= #{endUpdateTime, jdbcType=TIMESTAMP}


		and cutoff_date >= #{startCutoffDate,jdbcType=DATE}


    	and cutoff_date <= #{endCutoffDate, jdbcType=DATE}


		and is_cutoff = #{isCutoff,jdbcType=INTEGER}
